class digistoreAdminModeldigistoreConfig extends obModel
{
	var $_configs = null;
	var $_id = null;

	function __construct () {
		parent::__construct();
		$this->_id = 1;
	}

	function getConfigs() {
		if (empty ($this->_configs)) {
			$this->_configs = $this->getTable("digistoreConfig");
			$this->_configs->load($this->_id);
		}
		$f = $this->_configs->time_format;
		$f = str_replace ("MM", "m", $f);
		$f = str_replace ("DD", "d", $f);
		$f = str_replace ("YYYY", "Y", $f);
		$this->_configs->time_format = $f;

		return $this->_configs;
	}

	function store(){
		$item = $this->getTable('digistoreConfig');
		$data = JRequest::get('post');

		if(!isset($data["showtwitter"])){
			$data["showtwitter"] = 0;
		}
		if(!isset($data["showretwitter"])){
			$data["showretwitter"] = 0;
		}
		if(!isset($data["showfacebook"])){
			$data["showfacebook"] = 0;
		}

		if(!$item->bind($data)){
			// $item->setError($item->getErrorMsg());
			return JError::raiseWarning( 500, $item->getError() );
		}
		if (!$item->check()) {
			// $item->setError($item->getErrorMsg());
			return JError::raiseWarning( 500, $item->getError() );
		}

		if(!$item->store()){
			// $item->setError($item->getErrorMsg());
			return JError::raiseWarning( 500, $item->getError() );
		}

		return true;
	}

